		int a3,b3;
		a3=9;
		b3=4;
		System.out.println("a3+b3="+(a3+b3));
		System.out.println("a3-b3="+(a3-b3));
		System.out.println("a3*b3="+(a3*b3));
		System.out.println("a3/b3="+(a3/b3));
		
		long a4,b4;
		a4=56;
		b4=13;
		System.out.println("a4+b4="+(a4+b4));
		System.out.println("a4-b4="+(a4-b4));
